// src/Login.js
import React, { useState } from 'react';
import { Button, Form, Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaEye, FaEyeSlash } from 'react-icons/fa'; // Importing icons
import { useNavigate } from 'react-router-dom'; // Import useHistory

// Inside Login component
const history = useNavigate();

// After a successful login:




const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false); // State for password visibility

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5220/api/auth/login', {
                email,
                password,
            });
            // Handle success (e.g., redirect to dashboard)
            console.log(response.data);
            toast.success('Login successful!');  // Here is where we will add the functionality people ðŸ‘Œ
            navigate('/dashboard'); // Redirect to admin dashboard
        } catch (error) {
            console.error(error);
            toast.error('Login failed. Please check your credentials.');
        }
    };

    return (
        <Container className="mt-5">
            <Row className="justify-content-center">
                <Col md={6}>
                    <h2 className="text-center">Login</h2>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formEmail">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                type="email"
                                placeholder="Enter your email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Form.Group controlId="formPassword" className="mt-3 position-relative">
                            <Form.Label>Password</Form.Label>
                            <Form.Control
                                type={showPassword ? 'text' : 'password'} // Toggle between text and password
                                placeholder="Enter your password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                            {/* Eye icon to toggle password visibility */}
                            <span
                                onClick={() => setShowPassword(!showPassword)}
                                style={{
                                    position: 'absolute',
                                    right: '10px',
                                    top: '38px',
                                    cursor: 'pointer',
                                }}
                            >
                                {showPassword ? <FaEyeSlash /> : <FaEye />}
                            </span>
                        </Form.Group>

                        <Button variant="primary" type="submit" className="mt-4">
                            Login
                        </Button>
                    </Form>
                </Col>
            </Row>
        </Container>
    );
};

export default Login;
