// src/AdminDashboard.js
import React, { useEffect, useState } from 'react';
import { Button, Form, Table, Modal } from 'react-bootstrap';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
    
    const [employees, setEmployees] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [currentEmployee, setCurrentEmployee] = useState({ id: '', name: '', surname: '', jobTitle: '' });

    // Fetch employees when the component mounts
    useEffect(() => {
        fetchEmployees();
    }, []);

    const fetchEmployees = async () => {
        try {
            const response = await axios.get('http://localhost:5220/api/admin/employees');
            setEmployees(response.data);
        } catch (error) {
            console.error('Error fetching employees:', error);
            toast.error('Failed to fetch employees.');
        }
    };

    const handleShowModal = (employee) => {
        setCurrentEmployee(employee || { id: '', name: '', surname: '', jobTitle: '' });
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (currentEmployee.id) {
                // Update existing employee
                await axios.put(`http://localhost:5220/api/admin/update-employee/${currentEmployee.id}`, currentEmployee);
                toast.success('Employee updated successfully!');
            } else {
                // Create new employee
                await axios.post('http://localhost:5220/api/admin/create-employee', currentEmployee);
                toast.success('Employee created successfully!');
            }
            fetchEmployees();
            handleCloseModal();
        } catch (error) {
            console.error('Error saving employee:', error);
            toast.error('Failed to save employee.');
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5220/api/admin/delete-employee/${id}`);
            toast.success('Employee deleted successfully!');
            fetchEmployees();
        } catch (error) {
            console.error('Error deleting employee:', error);
            toast.error('Failed to delete employee.');
        }
    };

    return (
        <div>
            <h2 className="text-center mb-4">Admin Dashboard</h2>
            <Button onClick={() => handleShowModal()} variant="primary" className="mb-3">
                Add Employee
            </Button>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Job Title</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee) => (
                        <tr key={employee.id}>
                            <td>{employee.id}</td>
                            <td>{employee.name}</td>
                            <td>{employee.surname}</td>
                            <td>{employee.jobTitle}</td>
                            <td>
                                <Button variant="warning" onClick={() => handleShowModal(employee)}>Edit</Button>
                                <Button variant="danger" onClick={() => handleDelete(employee.id)} className="ml-2">Delete</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>

            {/* Modal for adding/editing employee */}
            <Modal show={showModal} onHide={handleCloseModal}>
                <Modal.Header closeButton>
                    <Modal.Title>{currentEmployee.id ? 'Edit Employee' : 'Add Employee'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formName">
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Enter name"
                                value={currentEmployee.name}
                                onChange={(e) => setCurrentEmployee({ ...currentEmployee, name: e.target.value })}
                                required
                            />
                        </Form.Group>
                        <Form.Group controlId="formSurname" className="mt-3">
                            <Form.Label>Surname</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Enter surname"
                                value={currentEmployee.surname}
                                onChange={(e) => setCurrentEmployee({ ...currentEmployee, surname: e.target.value })}
                                required
                            />
                        </Form.Group>
                        <Form.Group controlId="formJobTitle" className="mt-3">
                            <Form.Label>Job Title</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Enter job title"
                                value={currentEmployee.jobTitle}
                                onChange={(e) => setCurrentEmployee({ ...currentEmployee, jobTitle: e.target.value })}
                                required
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit" className="mt-3">
                            {currentEmployee.id ? 'Update Employee' : 'Add Employee'}
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </div>
    );
};

export default AdminDashboard;
