// src/components/Login.js
import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrorMessage(''); // Clear previous error message

        try {
            // Call your API endpoint to validate the employee's credentials
            const response = await axios.get('http://localhost:5220/api/admin/employees', {
                params: {
                    email,
                    password
                }
            });

            // Check if employee exists (handle response accordingly)
            if (response.data) {
                // Login successful
                alert(`You are a valid user`);
                // You can navigate to a different page or set user context here
            }
        } catch (error) {
            setErrorMessage('Invalid email or password. Please try again.');
            console.error('Login error:', error);
            alert(`You are NOT A VALID user`);
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Login</button>
            </form>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        </div>
    );
};

export default Login;
